var parser = require('./parser.js');
var evaluator = require('./evaluator.js');
var err = require('./error.js');

module.exports.err = err;

module.exports.parse = function(str) {
	/*
	*  This is the main interface function of the markup
	*  transpiler. This function passes 'str' down to the
	*  transpiler chain and returns the processed output
	*  string.
	*/
	return evaluator.eval(parser.parse(str));
}
