var $ = require('jquery');
var BaseValidator = require('./basevalidator.js');

module.exports = class EqValidator extends BaseValidator {
	/*
	*  Validate the value of the input using strict equality.
	*
	*  Settings:
	*    * value = The value to match against. If this value is
	*              a function, it's used as a getter.
	*/
	constructor(...args) {
		super(...args);
		this.chk_settings(['value']);
	}

	validate(element) {
		if (typeof this.settings.value === 'function') {
			return element.value === this.settings.value();
		} else {
			return element.value === this.settings.value;
		}
	}
}
